import { useEffect } from "react";
import ReactDOM from "react-dom";
import './AddProduct.css';

const AddProduct = ({closeModal}) => {
    useEffect(()=>{
        console.log("Hi");
        document.body.style.overflowY = "hidden";
        return ()=>{
            document.body.style.overflowY = "scroll";
        }
    },[]);
    
    return ReactDOM.createPortal(<>
        <div className="modal-wrapper" onClick={closeModal}>
            <div className="modal-container">
            <div className="content-tabs">
            <div className = {tab===1?"content  active-content": "content"}>
                <h2>Products</h2>
                <hr />
                <button onClick={()=>setshowModal(true)}>Add Products</button>
                {showModal && <AddProduct closeModal={closeModal}/>}
                <p>
                    My Products
                </p>
            </div>
            <div className={tab === 2 ? "content  active-content" : "content"}>
                        <h2>My Bids</h2>
                        <hr />
                            <p>
                                My Bids
                            </p>
            </div>
            <div className={tab === 3 ? "content  active-content" : "content"}>
                <h2>User Info</h2>
                <hr />
                <p>
                    User Info
                </p>
            </div>
        </div>
            </div>
        </div>
    </>, document.getElementById("myModal"));
}

export default AddProduct;
