import { useEffect } from "react";
import ReactDOM from "react-dom";

const AddProduct = ({closeModal}) => {
    useEffect(()=>{
        document.body.style.overflowY = "hidden";
        return ()=>{
            document.body.style.overflowY = "scroll";
        }
    },[]);
}

export default AddProduct;
