import React, { useEffect, useState, getState } from "react";
import { useNavigate} from "react-router-dom";
import axios from 'axios';
import Item from "../Item";
import "../Home/home.css";

function Home(){
    //Imports
    const navigate = useNavigate();
    
    //Use States
    const [items, setItems] = useState([]);
    const [username, setUsername] = useState();
    const [age_filter, setAge_filter] = useState([]);
    const [display_items, setDisplay_items] = useState([]);
    const [cat_Set, setCatSet] = useState(new Set());
    const [age_Set, setAgeSet] = useState(new Set());
    const [search_filter, setSearch_filter] = useState();
    
    const [items_1, setItems1] = useState([]);

    //Use Effects
    useEffect(()=>{
        const token = localStorage.getItem('token');
        axios.get("http://localhost:5000/", { headers : {
            Authorization: token,
        }}).then(async (res) =>{
            console.log(res);
            setUsername(res.data.username);
            setItems(res.data.items);
            setItems1(res.data.items);
            setDisplay_items(res.data.items);
        })
        .catch((error) => 
        {
            navigate('/login');
        });
    },[]);

    //On Click Functions

    function logout(){
        localStorage.removeItem('token');
        navigate('/login');
    }

    async function category_checkbox(event) {
        if(event.target.checked)
        {
            const newSet = cat_Set;
            const y = await newSet.add(event.target.name);
            setCatSet(newSet);

            filter_display_items();
        }
        else
        {
            const newSet = cat_Set;
            newSet.delete(event.target.name);
            setCatSet(newSet);

            filter_display_items();
        }
    }

    async function age_checkbox(event) {
        if(event.target.checked)
        {
            const newSet = age_Set;
            const y = await newSet.add(event.target.name);
            setAgeSet(newSet);

            filter_display_items();
        }
        else
        {
            const newSet = age_Set;
            const y = await newSet.delete(event.target.name);
            setAgeSet(newSet);
            filter_display_items();
        }
    }
  
    async function filter_display_items() {
        console.log(search_filter);
        setDisplay_items(items.filter((item) => {
            if(cat_Set.has(item.category) && age_Set.has(item.age) && item.name.toLowerCase().includes(search_filter))
                return true;
            if((cat_Set.size === 0 || cat_Set.has("category_all"))&& age_Set.has(item.age)&& item.name.toLowerCase().includes(search_filter))
                return true;
            if(cat_Set.has(item.category) && (age_Set.size === 0 || age_Set.has("age_all")) && item.name.toLowerCase().includes(search_filter))
                return true;
            if((cat_Set.size === 0 || cat_Set.has("category_all")) && (age_Set.size === 0 || age_Set.has("age_all"))&& item.name.toLowerCase().includes(search_filter))
                return true;
        }));    
    }
    async function search_box_changed(event){
        const newVall = event.target.value.toLowerCase();
        setTimeout(() => {
            setSearch_filter(newVall);
            if(search_filter === event.target.value.toLowerCase())
