            <td class="text-left">January</td>
            <td class="text-left">$ 50,000.00</td>
            <td class="text-left">$ 50,000.00</td>
            <td class="text-left">$ 50,000.00</td>
            <td class="text-left">$ 50,000.00</td>
            </tr>
            </tbody>
            </table>
            </div>
    </>, document.getElementById("myModal"));
};

export default ProductsBidsDisplay;
