import { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import axios from 'axios';

import "./Placebid.css";
import { redirect } from "react-router-dom";

function PlaceBid({item, user, closeModal}){
    
    const [amount, setAmount] = useState(-1);
    const [message, setMessage] = useState();
    
    function validate() {
        if (amount < 0) {
            alert("Enter the price");
            return;
        }
        else {
            handleSubmit();
        }
    }

    function handleSubmit() {
        // console.log(item);
        // console.log(user);
        // console.log(amount);
        // console.log(message);
        // console.log(new Date());

        const bid = {
            productID: item,
            bidderID: user._id,
            bidderName: user.name,
            price: amount,
            message: message,
            biddedOn: new Date()
        }
        console.log(bid);

        axios.post('http://localhost:5000/bids/upload', bid).then(async (res)=>{
            alert("Bid placed successfully");
            redirect(`/product/${item}`);
            closeModal();
        }).catch(err => {
            alert(err);
        })
    }

    return ReactDOM.createPortal( 
        <div><div onClick={closeModal} className="modal-wrapper_pb"></div>
        <div className="modal-containerr_pb">
            <div className="wrap-login100k">
                <div className="login100-form validate-form p-l-55 p-r-55k p-t-120">
                    <span className="login100-form-titlek">
                        Place Bid
                    </span>

                    <div className="wrap-input100 validate-input m-b-16">
                        <input className="input100l" type="number" placeholder="Enter Your bid/ Price" onChange={event => setAmount(event.target.value)} value={amount} />
                        <span className="focus-input100"></span>
                    </div>

                    <div className="wrap-input100 validate-input">
                        <textarea className="input100k" type="text" placeholder="Message" onChange={event => setMessage(event.target.value)} value={message} />
                        <span className="focus-input100"></span>
                    </div>
                    <div>
                    </div>

                    <div className="container-login100-form-btnk">
                        <button onClick={closeModal} className="login100-form-btnk">
                            Cancel
                        </button>
                        <button onClick={validate} className="login100-form-btnk">
                            Submit
                        </button>
                    </div>
                </div>
            </div>
        </div></div>
        , document.getElementById("myModal"));
}

export default PlaceBid;
