import React, { useEffect, useState } from "react";
import "../UsersProducts/UsersProducts.css";
import axios from 'axios';
import EditProduct from "../EditProduct/EditProduct";
import DeleteProduct from "../DeleteProduct/DeleteProduct";
import { Link } from "react-router-dom";

function UsersProducts(props) {
    const [editModal, seteditModal] = useState(false);
    const [deleteModal, setDeleteModal] = useState(false);
    function closeDltModal(){
      setDeleteModal(false);
    }
    const [products, setProducts] = useState([]);

    const [activeProducts, setActiveProducts] = useState([]);
    const [deletedProducts, setDeletedProducts] = useState([]);
    useEffect(()=>{
        const token = localStorage.getItem('token');
        
        axios.get("http://localhost:5000/owner/products", { headers : {
            Authorization: token,
        }}).then(async (res) =>{
            setProducts(res.data);
            setCorresProducts(res.data);
            // console.log(res.data);
        })
    }, []);

    function setCorresProducts(prods) {
      // console.log(prods);
      prods.forEach(prod => {
        if (prod.sold) {
          setDeletedProducts(prev => {
            return [...prev, prod];
          })
        }
        else{
          setActiveProducts(prev => {
            return [...prev, prod];
          })
        }
      });
      // setActiveProducts(prev => {
      //   return [...prev, ]
      // })
    }

    function itemDelete(index) {
      setDeleteModal(false);
      console.log(index);
      console.log(activeProducts[index]);
      axios.delete(`http://localhost:5000/product/${products[index]._id}`).then(res => {
        // console.log(res);
        if (res.data.status === "Success") {
          alert(res.data.message);
          // window.location.reload();
          // console.log();
        }
        if (res.data.status === "failed") {
          alert(res.data.message);
          // console.log();
        }
        // console.log("deletd")
      }).catch(err => {
        console.log(err);
      });
      setActiveProducts((products) =>
