import { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import './AddProduct.css';
import './Tabs/tabs.css';
const AddProduct = ({closeModal}) => {
    const [tab, setTab] = useState(1);
    const [image, setImage] = useState("");

    useEffect(()=>{
        console.log("Hi");
        document.body.style.overflowY = "hidden";
        return ()=>{
            document.body.style.overflowY = "scroll";
        }
    },[]);
    
    function btnClick(tab)
    {
        setTab(tab);
    }
    
    function submit_Img() {
        const data = new FormData()
        data.append("file", )
    }
    return ReactDOM.createPortal(<>
        <div className="modal-wrapper" onClick={closeModal}>
        </div>
            <div className="modal-containerr">
                <h1 className="abcdd">Add Product</h1>
                <div className="bloc-tabs">
                    <button className= {tab===1?"tabs active-tabs":"tabs"} onClick={()=>btnClick(1)}>General Info</button>
                    <button className={tab===2?"tabs active-tabs":"tabs"} onClick={()=>btnClick(2)}>Images</button>
                </div>
            <div className="content-tabs">
            <div className = {tab===1?"content  active-content": "content"}>
                <h2>General Info</h2>
                <hr />
                <div class="cont">
                    <div>
                        <label for="Name">Product Name</label>
                        <input type="text" id="prodName" name="Product Name" className="abcd" placeholder="Product Name"/>
                        
                        <label for="cost">Cost</label>
                        <input type="number" id="cost" className="abcd" name="cost" placeholder="Cost"/>

                        <label for="buyYear">Year of Buying</label>
                        <input type="number" id="year" name="year" className="abcd" placeholder="Year of Buying"/>

                        <label for="Category">Category</label>
                        <select className="abcd" name="category">
                            <option className="opts" value="electronics">Electronics</option>
                            <option className="opts" value="clothing">Cloting</option>
                            <option className="opts" value="sports">Sports</option>
                            <option className="opts" value="home">Home</option>
                        </select>
                        <div className="cstm" >
                            <div>
                            <span>Warranty available</span> <input className="Ask" name="warranty" type="checkbox" />
                            </div>
                            <div>
                            <span>Show Bids on Page</span> <input className="Ask" name="bids_show" type="checkbox" />
                            </div>
                        </div>
                        <label for="description">Description</label>
                        <textarea name="description" className="abcd" placeholder="Description about your product.." > </textarea>

                        <input type="submit" className="abcd" value="Submit"/>
                    </div>
                </div>
            </div>
            <div className={tab === 2 ? "content  active-content" : "content"}>
                <h2>Images Upload</h2>
                <hr />
                <input type="file" onChange={(e)=> console.log(e.target.files)}/>
                <button onClick={submit_Img}>Upload</button>
            </div>
            </div>
</div>
    </>, document.getElementById("myModal"));
}

export default AddProduct;
