import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../Home/home.css";
import "../Product/product.css";
import axios from 'axios';
// import logout from "../Functions/logout";

function Product() {
    const {id} = useParams();
    const navigate = useNavigate();
    const [data, setData] = useState({
        item: {
            name: "",
            category: "",
            cost: 0,
            ownerID: "",
            age: "",
            imageURL: [
                ""
            ],
            description: "",
            bids: []
        },
        user: {
            username: "",
        }
    });
    const [username, setUsername] = useState();    
    const [ownerDetails, setOwnerDetails] = useState({
        name: "",
        emailID: ""
    });
    function logout(){
        localStorage.removeItem('token');
        navigate('/login');
    }
    
    
    useEffect(()=>{
        console.log(id);
        
        const token = localStorage.getItem('token');
            axios.get( `http://localhost:5000/product/${id}`, { headers : {
                Authorization: token,
            }}).then(async (res) =>{
                owner
                console.log(res.data);
                setUsername(res.data.user.username)
                setData(res.data);
                console.log(data.item);
                // console.log(res.data.item.name);
            })
            .catch((error) => 
            {
                // console.log(error);
                navigate('/login');
            });
    },[]);

    function ownerFetch(ownerId) {
        const token = localStorage.getItem('token');
            axios.get( `http://localhost:5000/owner/${ownerId}`, { headers : {
                Authorization: token,
            }}).then(async (res) =>{

            })
            .catch((error) => 
            {
                console.error("Not authorized");
            });
    }

    return <div>
        <div className="nav">
            <h1 className="appName">MEGHA</h1>
            <div className="routes">
                <i>
                    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-bell" viewBox="0 0 16 16">
                        <path d="M8 16a2 2 0 0 0 2-2H6a2 2 0 0 0 2 2zM8 1.918l-.797.161A4.002 4.002 0 0 0 4 6c0 .628-.134 2.197-.459 3.742-.16.767-.376 1.566-.663 2.258h10.244c-.287-.692-.502-1.49-.663-2.258C12.134 8.197 12 6.628 12 6a4.002 4.002 0 0 0-3.203-3.92L8 1.917zM14.22 12c.223.447.481.801.78 1H1c.299-.199.557-.553.78-1C2.68 10.2 3 6.88 3 6c0-2.42 1.72-4.44 4.005-4.901a1 1 0 1 1 1.99 0A5.002 5.002 0 0 1 13 6c0 .88.32 4.2 1.22 6z" />
                    </svg>
                </i>
                <span className="username">
                    {username}
                </span>
                <div className="logout">
                    <i color="black">

                        <svg onClick={logout} xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="black" className="bi bi-box-arrow-right" viewBox="0 0 16 16"></svg>
                    </i>
                </div>
            </div>
        </div>
     <div className="a" >
        <div>
            <div className="b">
                <div className="c">
                    <img className="d" src="http://res.cloudinary.com/sathya195/image/upload/v1677935682/fcp5o0km0kvk9wwunlsl.jpg" alt="product" />
                    <div className="e" >
                        <img className="f" src="http://res.cloudinary.com/sathya195/image/upload/v1677935682/fcp5o0km0kvk9wwunlsl.jpg" alt="product"/>
                        <img className="g"  src="http://res.cloudinary.com/sathya195/image/upload/v1677935691/i713mcli0yanrno2buoq.jpg" alt="product"/>
                        <img className="g"  src="http://res.cloudinary.com/sathya195/image/upload/v1677935701/ikxeis6brdlrg5nihwsd.jpg" alt="product"/>
                    </div>
