import React, { useEffect, useState } from "react";
import { Navigate, useNavigate} from "react-router-dom";
import axios from 'axios';
import Item from "../Item";
import "../Home/home.css";

function Home(){
    //Imports
    const navigate = useNavigate();
    
    //Use States
    const [items, setItems] = useState([]);
    const [userName, setUsername] = useState("");
    const [category_filter, setCategory_filter] = useState([]);
    const [display_items, setDisplay_items] = useState([]);
    //Use Effects
    useEffect(()=>{
        const token = localStorage.getItem('token');
         axios.get("http://localhost:5000/", { headers : {
            Authorization: token,
        }}).then(async (res) =>{
            // console.log(res.data);
            setItems(res.data.items);
            setDisplay_items(res.data.items);
            setUsername(res.data.username);
        })
        .catch((error) => 
        {
            navigate('/login');
        });
    },[]);

    //On Click Functions

    function logout(){
        localStorage.removeItem('token');
        navigate('/login');
    }

    async function category_checkbox(event) {
        if(event.target.checked)
        {
            const newAr = await [...category_filter, event.target.name];
            setCategory_filter(newAr);
        }
        else
        {
            setCategory_filter((prevArray) => {
                return prevArray.filter((el) => {
                    return el != event.target.name;
                }); 
            });
        }
        filter_display_items();
    }

    async function filter_display_items() {
        //category
        console.log(category_filter);
        // console.log(items);
        setDisplay_items([]);
        const arr = await items.filter((item) => {
            category_filter.forEach(cat => {
                if(item.category === cat)
                {
                    console.log("Haa");
                    setDisplay_items(prevArray => {
                        return [...prevArray, item]
                    });
                }    
                // console.log(cat, item['category']);
            })
        });
        console.log(arr);
        // setDisplay_items( );

            // return false;
        // }) 
        // console.log(display_items);
        //age

    }


    return (<div>
        <div className="total">
            <div className="nav">
                <h1 className="appName">MP</h1>
                <div onClick={logout} className="routes">
                    <i>
                    <svg onClick={logout} xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-bell" viewBox="0 0 16 16">
                <path d="M8 16a2 2 0 0 0 2-2H6a2 2 0 0 0 2 2zM8 1.918l-.797.161A4.002 4.002 0 0 0 4 6c0 .628-.134 2.197-.459 3.742-.16.767-.376 1.566-.663 2.258h10.244c-.287-.692-.502-1.49-.663-2.258C12.134 8.197 12 6.628 12 6a4.002 4.002 0 0 0-3.203-3.92L8 1.917zM14.22 12c.223.447.481.801.78 1H1c.299-.199.557-.553.78-1C2.68 10.2 3 6.88 3 6c0-2.42 1.72-4.44 4.005-4.901a1 1 0 1 1 1.99 0A5.002 5.002 0 0 1 13 6c0 .88.32 4.2 1.22 6z"/>
            </svg>
                    </i>
                    <span className="username">
                        MEGHA
                    </span>
                    <i color="black">
                    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-box-arrow-right" viewBox="0 0 16 16"></svg>
                    </i>
                </div>
            </div>
            <div className="down"><div className="divide">
                <div className="filters">
                    <div className="heading">
                        <span className="heading_name">Filters</span>
                        <span>X</span>
                    </div>
                    <div className="category">
                        <span className="category-text">Category</span>
                        <div className="category-items">
                            <input type="checkbox" name="category_all" onChange={category_checkbox} className="cat-items-input" />
                            <span>All</span>
                        </div>
                        <div className="category-items">
                            <input type="checkbox" name="electronics" onChange={category_checkbox} className="cat-items-input" />
                            <span>Electronics</span>
                        </div>
                        <div className="category-items">
                            <input type="checkbox" name="Clothing" onChange={category_checkbox} className="cat-items-input" />
                            <span>Clothing</span>
                        </div>
                        <div className="category-items">
                            <input type="checkbox" name="Sports" onChange={category_checkbox} className="cat-items-input" />
                            <span>Sports</span>
                        </div>
                        <div className="category-items">
                            <input type="checkbox" name="Home" onChange={category_checkbox} className="cat-items-input" />
                            <span>Home</span>
                        </div>
                    </div>
