import React, { useEffect, useState } from "react";
import { useNavigate} from "react-router-dom";
import axios from 'axios';
import Item from "../Item";
import "../Home/home.css";

function Home(){
    //Imports
    const navigate = useNavigate();
    
    //Use States
    const [items, setItems] = useState([]);
    const [category_filter, setCategory_filter] = useState([]);
    const [age_filter, setAge_filter] = useState([]);
    const [display_items, setDisplay_items] = useState([]);
    //Use Effects
    useEffect(()=>{
        const token = localStorage.getItem('token');
         axios.get("http://localhost:5000/", { headers : {
            Authorization: token,
        }}).then(async (res) =>{
            // console.log(res.data);
            setItems(res.data.items);
            setDisplay_items(res.data.items);
            // setUsername(res.data.username);
        })
        .catch((error) => 
        {
            navigate('/login');
        });
    },[]);

    //On Click Functions

    function logout(){
        localStorage.removeItem('token');
        navigate('/login');
    }

    async function category_checkbox(event) {
        if(event.target.checked)
        {
            const newAr = category_filter;
            const x = await newAr.push(event.target.name);
            setCategory_filter(newAr);
            console.log(category_filter);
            filter_display_items();
        }
        else
        {
            const newAr = category_filter;
            var index = await newAr.indexOf(event.target.name);
            if (index !== -1) {
                await newAr.splice(index, 1);
            }
            setCategory_filter(newAr);
            console.log(category_filter);
            filter_display_items();
        }
    }

    async function age_checkbox(event) {
        if(event.target.checked)
        {
            const newAr = age_filter;
            const x = await newAr.push(event.target.name);
            setAge_filter(newAr);
            console.log(age_filter);
            filter_display_items();
        }
        else
        {
            const newAr = age_filter;
            var index = await newAr.indexOf(event.target.name);
            if (index !== -1) {
                await newAr.splice(index, 1);
            }
            setAge_filter(newAr);
            console.log(age_filter);
            filter_display_items();
        }
    }

    const [items_1, setItems1] = useState([]);
    async function filter_display_items() {
        setDisplay_items([]);
        setItems1([]);
        var index = category_filter.indexOf("category_all");
        var index1 = age_filter.indexOf("age_all");
        if(category_filter.length === 0)
        {
            // setDisplay_items(items);
            setItems1(items);
            // console.log(item);
        }
        else if (index !== -1) {
            // setDisplay_items(items);
            setItems1(items);
            // console.log(items_1);
        }
